    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        long h;
        h = (Long.valueOf(Hours.getText()));
        double days;
        days = (Double.valueOf(Hours.getText())) / 24;
        this.hours.setText("It is " + h + " hours.");
        this.Days.setText("It is equal to " + days + " days.");
    }//GEN-LAST:event_jButton1ActionPerformed

